    //leetcode submit region begin(Prohibit modification and deletion)
    class Solution {
        public int threeSumMulti(int[] arr, int target) {
            int mark = 1_000_000_007;
            long ans = 0;
            int[] count = new int[101];
            // 统计每个数字出现的次数
            for (int i : arr) {
                count[i]++;
            }

            // 确定每一个数字,找可能的数字
            for (int i = 0; i <= 100; i++) {
                for (int j = i; j <= 100; j++) {
                    // 第三个数字
                    int k = target - i - j;
                    if (k < 0) {
                        break;
                    }
                    if (k > 100 || k < j || count[k] == 0) {
                        // 没有找到第三个数字
                        continue;
                    }


                    //  m      n!         n(n-1)(n-2)....(n-m+1)
                    //C  = --------- = --------------------------
                    // n    (n-m)!m!       m(m-1)(m-2)....1
                    // 从大的数字开始往小乘，乘“小的数字那么多”个，再除以“小的数字开始往小乘，乘小的数字那么多个”
                    if (i == j && j == k) {
                        if (count[i] < 3) {
                            continue;
                        }
                        // n 选 3
                        ans += (long) (count[i] - 2) * (count[i] - 1) * (count[i]) / 6;
                        continue;
                    }

                    if (i == j) {
                        // n 选 2
                        ans += (long) (count[i]) * (count[i] - 1) / 2 * count[k];
                        continue;
                    }

                    if (j == k) {
                        ans += (long) (count[j]) * (count[j] - 1) / 2 * count[i];
                        continue;
                    }

                    ans += (long) count[i] * count[j] * count[k];
                }
            }
            return (int) (ans % mark);
        }
    }
//leetcode submit region end(Prohibit modification and deletion)

